<?php require __DIR__.'/../app/bootstrap.php'; if($_SERVER['REQUEST_METHOD']==='POST'){ if(login($_POST['username']??'', $_POST['password']??'')){ header('Location: /'); exit; } $err='Invalid'; } include __DIR__.'/../views/partials/header.php'; ?><div class='container mt-5' style='max-width:420px'><h3>Login</h3><?php if(!empty($err)) echo '<div class=alert alert-danger>'.$err.'</div>'; ?><form method='post'><?php csrf_field(); ?><input class='form-control mb-2' name='username' placeholder='username'><input class='form-control mb-2' type='password' name='password' placeholder='password'><button class='btn btn-primary w-100'>Login</button></form></div><?php include __DIR__.'/../views/partials/footer.php'; ?>
